from django.conf.urls import urlfrom . import viewsurlpatterns = [    # 获取扫码url    url(r'^oauth/qq/authorization/$', views.QQAuthURLView.as_view()),    # 扫码后回调处理    url(r'^oauth/qq/user/$', views.QQAuthUserView.as_view()),    # 获取扫码url    url(r'^oauth/weibo/authorization/$', views.WeiboAuthURLView.as_view()),    # 扫码后回调    url(r'^oauth/weibo/user/$', views.WeiboAuthUserView.as_view()),    # 验证码    url(r'^image_codes/(?P<image_code_id>.+)/$', views.get_image)]